mapper.select:
select 

from sys_user_menu_rel
where user_id = #{userId,jdbcType=INTEGER}
  and menu_id = #{menuId,jdbcType=INTEGER}

mapper.insert:

  insert into sys_user_menu_rel (user_id, project_id, menu_id, create_time
    , create_user)   
  values  
    
      (#{item.userId,jdbcType=INTEGER}, #{item.projectId,jdbcType=INTEGER}, #{item.menuId,jdbcType=INTEGER}, #{item.createTime,jdbcType=TIMESTAMP}, 
     #{item.createUser,jdbcType=INTEGER}
    )

mapper.delete:
    delete from sys_user_menu_rel
    where user_id = #{userId,jdbcType=INTEGER}
    AND project_id in
	(SELECT DISTINCT (pgr.project_id)
	FROM sys_user_group_rel ugr, sys_project_group pg, sys_project_group_rel pgr
	WHERE ugr.group_id = pg.id AND pgr.group_id = pg.id
	AND ugr.user_id = #{managerId,jdbcType=INTEGER}
	)

mapper.insert:
insert into sys_user_menu_rel (user_id, menu_id, create_time, 
  update_time, create_user, update_user
  )
values (#{userId,jdbcType=INTEGER}, #{menuId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=INTEGER}, #{updateUser,jdbcType=INTEGER}
  )

mapper.update:
update sys_user_menu_rel

  
    create_time = #{createTime,jdbcType=TIMESTAMP},
  
  
    update_time = #{updateTime,jdbcType=TIMESTAMP},
  
  
    create_user = #{createUser,jdbcType=INTEGER},
  
  
    update_user = #{updateUser,jdbcType=INTEGER},
  

where user_id = #{userId,jdbcType=INTEGER}
  and menu_id = #{menuId,jdbcType=INTEGER}
  and project_id = #{project_id,jdbcType=INTEGER}

update sys_user_menu_rel
set create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  create_user = #{createUser,jdbcType=INTEGER},
  update_user = #{updateUser,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}
  and menu_id = #{menuId,jdbcType=INTEGER}
  and project_id= #{projectId,jdbcType=INTEGER}
